/**
 * Modules
 */

var foreach = require('@f/foreach')

/**
 * Expose assign
 */

module.exports = assign['default'] = assign

/**
 * Assign properties from source to target.
 * @param  {Object} target Target to copy `source` properties to.
 * @param  {Object} source
 * @return {Object}
 */

function assign (target, source) {
  for (var i = 1; i < arguments.length; ++i) {
    source = arguments[i]
    foreach(assignProp, source)
  }
  return target

  function assignProp(val, key) {
    target[key] = val
  }
}
